import echarts from 'echarts';
import { on, off } from '@/libs/tools';
import { getOrderSummaryData } from '@/api/data';
export default {
  name: 'serviceRequests',
  props: {
    text: String,
    subtext: String
  },
  data: function data() {
    return {
      dom: null
    };
  },
  methods: {
    resize: function resize() {
      this.dom.resize();
    }
  },
  mounted: function mounted() {
    var _this = this;

    getOrderSummaryData('user2').then(function (res) {
      var option = {
        title: {
          textStyle: {
            fontSize: 16
          },
          text: _this.text,
          subtext: _this.subtext,
          x: 'center'
        },
        tooltip: {
          trigger: 'axis',
          axisPointer: {
            type: 'cross',
            label: {
              backgroundColor: '#6a7985'
            }
          }
        },
        legend: {
          x: 'left',
          // 'center' | 'left' | {number},
          y: 'top',
          // 'center' | 'bottom' | {number}
          icon: 'rect',
          textStyle: {
            fontSize: 16
          },
          data: ['今日订单', '昨日订单']
        },
        grid: {
          top: '20%',
          left: '1.2%',
          right: '1%',
          bottom: '3%',
          containLabel: true
        },
        xAxis: [{
          type: 'category',
          boundaryGap: false,
          axisLabel: {
            show: true,
            interval: 'auto',
            textStyle: {
              color: '#800080',
              fontFamily: 'verdana',
              fontSize: 16,
              fontStyle: 'normal',
              fontWeight: 'bold'
            }
          },
          data: ['0时', '1时', '3时', '4时', '5时', '6时', '7时', '8时', '9时', '10时', '11时', '12时', '13时', '14时', '15时', '16时', '17时', '18时', '19时', '20时', '21时', '22时', '23时']
        }],
        yAxis: [{
          name: '订单数',
          type: 'value'
        }],
        series: [{
          name: '昨日订单',
          type: 'line',
          data: res.yesterdayOrderData
        }, {
          name: '今日订单',
          type: 'line',
          data: res.todayOrderData
        }]
      };

      _this.$nextTick(function () {
        _this.dom = echarts.init(_this.$refs.dom);

        _this.dom.setOption(option);

        on(window, 'resize', _this.resize());
      });
    });
  },
  beforeDestroy: function beforeDestroy() {
    off(window, 'resize', this.resize());
  }
};
