result:
.common{
  position: absolute;
  top: 0px;
  height: 100%;
  display: table;
  text-align: center;
}
.size{
  width: 100%;
  height: 100%;
}
.middle-center{
  display: table-cell;
  vertical-align: middle;
}
.info-card-wrapper{
  .size;
  overflow: hidden;
  .ivu-card-body{
    .size;
  }
  .content-con{
    .size;
    position: relative;
    .left-area{
      .common;
      left: 0px;
      & > .icon{
        .middle-center;
      }
    }
    .right-area{
      .common;
      right: 0px;
      & > div{
        .middle-center;
      }
    }
  }
}
